import React, { useState } from "react";
import axios from "axios";




export default function Forecast(props) {
let [loaded, setLoaded] = useState(false);
let [forecast, setForecast] = useState(null);

function handleResponse(response) {
  setForecast(response.data.daily);
  setLoaded(true);
}



if (loaded) {
console.log(forecast);

return (
  <div className="forecast-week">
    <div className="col" id="forecast-col">
      <div className="forecast-day">Monday</div>
      <img
        className="forecast-icon"
        src="http://openweathermap.org/img/wn/${forecast[0].weather[0].icon}@2x.png"
        alt={forecast[0].weather[0].description}
      />

      <div>
        <span className="high">{Math.round(forecast[0].temp.max)}°</span>{" "}
        <span className="low">{Math.round(forecast[0].temp.min)}°</span>
      </div>
    </div>
  </div>
);
}

else {

  let apiKey = "46fac47dd8b8fa26d1b6852218ad3dfe";
let rootUrl = "https://api.openweathermap.org/data/2.5/onecall?";
let lat = props.coords.lat;
let lon = props.coords.lon;
let units = "metric";
let apiUrl = `${rootUrl}lat=${lat}&lon=${lon}&appid=${apiKey}&units=${units}`;
console.log(apiUrl);

axios.get(apiUrl).then(handleResponse);

  return(null);

}
}
